import { useParams } from 'react-router-dom';
import axios from "axios";
import { useState } from "react";
import Button from 'react-bootstrap/Button';

export default function NFTPage(props) {
    const [data, updateData] = useState([]);
    const [dataFetched, updateDataFetched] = useState(false);

    const [message, updateMessage] = useState("");
    const [own, setOwn] = useState(false);
    const [currAddress, updateCurrAddress] = useState("0x");
    const [royaltyPercentage, setRoyaltyPercentage] = useState(''); // New state variable for royalty percentage

    async function getNFTData(tokenId) {
        let contract = props.contract;

        const tokenURI = await contract.tokenURI(tokenId);
        const listedToken = await contract.getListedTokenForId(tokenId);
        const tokenRoyaltyPercentage = await contract.tokenRoyalties(tokenId);

        let meta = await axios(tokenURI, {
            method: 'GET',
            mode: 'cors'
        });

        meta = meta.data;
        let image = getIPFSGatewayURL(meta.image);

        let item = {
            price: meta.price,
            tokenId: tokenId,
            seller: listedToken.seller,
            owner: listedToken.owner,
            image: image,
            name: meta.name,
            description: meta.description,
        };
        updateData(item);
        setRoyaltyPercentage(tokenRoyaltyPercentage); // Set the royalty percentage state variable

        if (props.account.toLowerCase() === data.seller.toLowerCase() || props.account.toLowerCase() === data.seller.toLowerCase()) {
            setOwn(true);
        }

        updateDataFetched(true);
    }

    async function buyNFT(tokenId) {
        try {
            console.log("Buy NFT function started"); // Add this
            const ethers = require("ethers");
            //Pull the deployed contract instance
            let contract = props.contract;
            const salePrice = ethers.utils.parseUnits(data.price, 'ether');
            console.log("Sale price:", salePrice); // Add this
            updateMessage("Buying the NFT... Please Wait (Upto 5 mins)");
            //run the executeSale function
            let transaction = await contract.executeSale(tokenId, { value: salePrice });
            console.log("Transaction submitted"); // Add this
            await transaction.wait();
            console.log("Transaction confirmed"); // Add this
            alert('You successfully bought the NFT!');
            updateMessage("");
            if (props.account.toLowerCase() === data.seller.toLowerCase() || props.account.toLowerCase() === data.seller.toLowerCase()) {
                setOwn(true);
            } else {
                setOwn(false);
            }
        } catch (e) {
            console.error("Error during NFT purchase:", e); // Log the error
            alert("NFT purchase error: " + e.message); // Display the error message
        }
    }
    

    const params = useParams();
    const tokenId = params.tokenId;
    if (!dataFetched) getNFTData(tokenId);

    const getIPFSGatewayURL = (ipfsURL) => {
        let urlArray = ipfsURL.split("/");
        let ipfsGateWayURL = `https://${urlArray[2]}.ipfs.dweb.link/${urlArray[3]}`;
        return ipfsGateWayURL;
    }

    return (
        <>
            <center>
                <div class="row row-cols-1 row-cols-md-1 g-4 mx-5 ">
                    <div class="col">
                        <div class="card h-100 w-50 ">
                            <img src={data.image} class="card-img-top" />
                            <div class="card-body text-left">
                                <div>
                                    <span className='text-info font-weight-bold'>Name:</span>{' '}{data.name}
                                </div>
                                <div>
                                    <span className='text-info font-weight-bold'>Description:</span>{' '}{data.description}
                                </div>
                                <div>
                                    <span className='text-info font-weight-bold'>Price:</span>{' '}{data.price + " ETH"}
                                </div>
                                <div>
                                    <span className='text-info font-weight-bold'>Owner:</span>{' '}{data.owner}
                                </div>
                                <div>
                                    <span className='text-info font-weight-bold'>Seller:</span>{' '}{data.seller}
                                </div>
                                <div>
                                    <span className='text-info font-weight-bold'>Royalty Percentage:</span>{' '}{royaltyPercentage}%
                                </div>
                                <br />
                                <div className="d-grid gap-2">
                                    {own ? (
                                        <div className="font-weight-bold text-success">You are the owner of this NFT</div>
                                    ) : (
                                        <Button variant="primary" size="lg" onClick={() => buyNFT(tokenId)}>Buy this NFT</Button>
                                    )}
                                    <div className="font-weight-bold">{message}</div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </center>
        </>
    )
}
